(590, 'HRA 2010 Rehire Of Employee', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-RG1Td01IdVFTUkE', '<p>To allow former employees with relevant job knowledge and skills required to fill current job postings in the company.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:10:15', '2016-02-25 13:48:47', 'hra-2010-rehire-of-employee', 0, NULL, NULL, NULL, NULL),
(591, 'HRA 2011 Absenteeism & Tardiness', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-ZzBEbmJmSzB3UDQ', '<p>To encourage acceptable standards of attendance among employees.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:11:08', '2016-02-25 13:50:20', 'hra-2011-absenteeism-tardiness', 0, NULL, NULL, NULL, NULL),
(592, 'HRA 3001 Annual Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-Yk9TeXVkaFo2Q0U', '<p>This policy outlines the conditions under which employees become eligible for paid annual leave.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:11:45', '2016-02-25 13:51:08', 'hra-3001-annual-leave-3', 0, NULL, NULL, NULL, NULL),
(593, 'HRA 3002 Medical Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-R3lkSWVlTUxUX0U', '<p>The objective of this policy is to set forth the procedures and conditions under which employees may be granted medical leave with pay in the event of personal illness or injury.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:14:23', '2016-02-25 13:59:40', 'hra-3002-medical-leave-5', 0, NULL, NULL, NULL, NULL),
(594, 'HRA 3005 National Service Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-UlJabV9VWlQzSW8', '<p>To prescribe policies and procedures for national service leave. This policy outlines the conditions under which employees become eligible for paid national service leave.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 39, '2016-01-27 08:23:37', '2016-02-01 18:04:00', 'hra-3005-national-service-leave', 0, NULL, NULL, NULL, NULL),
(595, 'HRA 3006 Maternity Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-Vm5EMC1Pd2ZrLTg', '<p>This policy sets forth conditions under which a female employee is entitled to Maternity Leave.</p>\r\n', 1, 2, 7, 'policy', NULL, NULL, NULL, NULL, 39, '2016-01-27 08:24:16', '2016-02-01 18:04:07', 'hra-3006-maternity-leave', 0, NULL, NULL, NULL, NULL),
