(713, 'HRA 3008 Childcare Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-alc1QnNkaS1fZDQ', '<p>This policy sets forth conditions under which an employee is entitled to childcare leave.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 44, '2016-01-27 08:33:07', '2016-01-27 17:33:07', 'hra-3008-childcare-leave', 0, NULL, NULL, NULL, NULL),
(712, 'HRA 3007 Paternity Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-OWRLQnE0dVRNYms', '<p>This policy sets forth conditions under which a male employee is entitled to paternity leave.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 39, '2016-01-27 08:25:12', '2016-01-27 17:25:12', 'hra-3007-paternity-leave', 0, NULL, NULL, NULL, NULL),
