(710, 'HRA 3005 National Service Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-UlJabV9VWlQzSW8', '<p>To prescribe policies and procedures for national service leave. This policy outlines the conditions under which employees become eligible for paid national service leave.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 39, '2016-01-27 08:23:37', '2016-01-27 17:23:37', 'hra-3005-national-service-leave', 0, NULL, NULL, NULL, NULL),
(708, 'HRA 3001 Annual Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-Yk9TeXVkaFo2Q0U', '<p>This policy outlines the conditions under which employees become eligible for paid annual leave.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:11:45', '2016-02-16 10:10:44', 'hra-3001-annual-leave-1', 0, NULL, NULL, NULL, NULL),
(709, 'HRA 3002 Medical Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-R3lkSWVlTUxUX0U', '<p>The objective of this policy is to set forth the procedures and conditions under which employees may be granted medical leave with pay in the event of personal illness or injury.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:14:23', '2016-02-16 10:11:28', 'hra-3002-medical-leave-2', 0, NULL, NULL, NULL, NULL),
(707, 'HRA 2011 Absenteeism & Tardiness', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-ZzBEbmJmSzB3UDQ', '<p>To encourage acceptable standards of attendance among employees.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:11:08', '2016-02-16 10:12:31', 'hra-2011-absenteeism-tardiness', 0, NULL, NULL, NULL, NULL),
(705, 'HRA 3004 Marriage Leave', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-QW5FYklhVEFmYXc', '<p>This policy outlines the conditions under which employees become eligible for paid marriage leave.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 39, '2016-01-27 08:21:16', '2016-02-16 10:13:27', 'hra-3004-marriage-leave', 0, NULL, NULL, NULL, NULL),
(706, 'HRA 2010 Rehire Of Employee', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-RG1Td01IdVFTUkE', '<p>To allow former employees with relevant job knowledge and skills required to fill current job postings in the company.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 34, '2016-01-27 08:10:15', '2016-02-16 10:16:20', 'hra-2010-rehire-of-employee', 0, NULL, NULL, NULL, NULL),
