(700, 'HRA 2006 Promotion ', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-VlBjeWlNMG5uQ0E', '<p>To prescribe policies and procedures for employee&rsquo;s promotion.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 29, '2016-01-27 08:01:05', '2016-02-16 10:21:18', 'hra-2006-promotion', 0, NULL, NULL, NULL, NULL),
(697, 'HRA 2007 Re-employment & Retirement', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-dWJxSzRIdnVqUDA', '<p>To prescribe policies and procedures for re-employment &amp; retirement.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 29, '2016-01-27 08:02:03', '2016-01-27 17:02:03', 'hra-2007-re-employment-retirement', 0, NULL, NULL, NULL, NULL),
