(738, 'HRA 2004 Hours Of Work', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-a2dGRXl2Q0ZKaXM', '<p>&ldquo;Hours of work&rdquo; refers to the company&rsquo;s normal working hours of a normal work day, required of its employees.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 29, '2016-01-27 07:39:50', '2016-02-16 10:18:20', 'hra-2004-hours-of-work-1', 0, NULL, NULL, NULL, NULL),
(739, 'HRA 2002 Recruitment', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-dHFTNUp4SGFNNUU', '<p>To describe the methods and procedures used by the company to fill the manpower needs of the company.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 24, '2016-01-27 07:33:48', '2016-01-27 16:33:48', 'hra-2002-recruitment', 0, NULL, NULL, NULL, NULL),
