(741, 'HRA 10006 Satefy & Hygiene Regulations', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-ME1LSnlyaFQyYms', '<p>It is the Company&rsquo;s policy to provide safe working conditions for all employees and to put in place, safety procedures and the necessary precautionary measures so as to minimize the occurrence of accidents in the course of work.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 19, '2016-01-27 07:26:42', '2016-02-01 17:28:38', 'hra-10006-satefy-hygiene-regulations', 0, NULL, NULL, NULL, NULL),
(742, 'HRA 10007 Whistle-Blowing Policy ', 'https://drive.google.com/a/verzdesign.com.sg/file/d/0ByttFUrfYli-T2I3N0h3bldRRDQ', '<p>To establish a group policy and guideline in which employees of the company and its subsidiaries may, in confidence, raise concerns about possible improprieties in matters of financial reporting or other matters and arrangements are in place for the independent investigation of such matters and appropriate follow up action.</p>\r\n', 1, 2, 6, 'policy', NULL, NULL, NULL, NULL, 19, '2016-01-27 07:27:44', '2016-01-27 16:27:44', 'hra-10007-whistle-blowing-policy-1', 0, NULL, NULL, NULL, NULL),
